    totalCount
    edges {
      node {
        id
        title
        date(format: "MMMM D, Y")
        summary
        timeToRead
        path
      }
    }
  }
}
</page-query>

<script>
import axios from "axios";

export default {
  metaInfo: {
    title: "Home",
  },
  data() {
    return {
      submit: "submit",
    };
  },
  methods: {
    sendEmail(evt) {
      this.submit = "sending...";
      const form = new FormData(evt.target);
      axios
        .post(process.env.GRIDSOME_FORMSPREE, form)
        .then((response) => {
          evt.target.reset();
          this.submit = "submit";
        })
        .catch((error) => {
          this.submit = "submit";
          this.$gtag.exception({
            description: "Contact form fail",
            fatal: false,
          });
        });
    },
  },
};
</script>
